import React from "react";
// import { Button } from "react-bootstrap";
import { useForm, register } from "react-hook-form"; // react-hook-form import

function UseForm({
  changeInput,
  inputData,
  onSaveButtonClick,
  resetForm,
  onSearchButtonClick,
}) {
  const { handleSubmit } = useForm(); // useForm 의 handleSubmit 가져옴.

  const saveBtnClick = () => {
    // preventDefault() 제거
    onSaveButtonClick(inputData);
    resetForm();
  };

  return (
    <div>
      {/* saveBtnClick 을 handleSubmit 으로 감쌈 */}
      <form onSubmit={handleSubmit(saveBtnClick)}>
        <h3></h3>
        <div>
          제목 :{" "}
          <input
            type="text"
            name="boardTitle"
            ref={register({ required: true })}
            onChange={changeInput}
            value={inputData.boardTitle}
          />
        </div>
        <div>
          내용 :{" "}
          <input
            type="text"
            name="boardContent"
            ref={register({ required: true, minLength: 5 })}
            onChange={changeInput}
            value={inputData.boardContent}
          />
        </div>
        <input
          type="hidden"
          name="boardId"
          onChange={changeInput}
          value={inputData.boardId}
        />
        <div>
          <h3></h3>
          <Button color="red" onClick={onSearchButtonClick}>
            조회
          </Button>
          <Button type="submit">저장</Button>
        </div>
      </form>
    </div>
  );
}

export default UseForm;
