import React, { useState, useEffect } from 'react';


function More() {
  const [moreFirst, setmoreFirst] = useState("");
  const [moreOper, setmoreOper] = useState("");
  const [moreSecond, setmoreSecond] = useState("");
  const [moreresult, setmoreResult] = useState("");


//   const calcul = () => {
//     if (operator === "+"){
//       setResult(parseInt(first)+parseInt(second))      
//     }else if(operator === "-"){
//       setResult(parseInt(first)-parseInt(second))     
//     }else if(operator === "*"){
//       setResult(parseInt(first)*parseInt(second))     
//     }else if(operator === "%"){
//       setResult(parseInt(first)%parseInt(second))    
//     }
//   }

    const clickNum = (e) => {
        if(!moreOper){
            setmoreFirst(
                ...moreFirst,
                e.target.value
            )
        }
    }

  return (
    <div className="more">
      <span>{moreFirst}</span>
      <span>{moreOper}</span>
      <span>{moreSecond}</span>
        <br />
      <button onClick={(e)=>clickNum(e)} value="0">0</button>
      <button>1</button>
      <button>2</button>
      <button>3</button>
      <button>4</button>
      <button>5</button>
      <button>6</button>
      <button>7</button>
      <button>8</button>
      <button>9</button>

      <button onClick={(e)=>setmoreOper(e.target.value)} value="+">+</button>
      <button onClick={(e)=>setmoreOper(e.target.value)} value="-">-</button>
      <button onClick={(e)=>setmoreOper(e.target.value)} value="*">*</button>
      <button onClick={(e)=>setmoreOper(e.target.value)} value="/">/</button>
      <button onClick={(e)=>setmoreOper(e.target.value)} value="%">%</button>
      <br />
      <button>calculator</button>
    </div>
  );
}

export default More;
